function getUserGroups(assigned_to, found_groups) {
  return frappe.call('frappe.client.get_list', {
    doctype: 'User Group',
    fields: ['name'],
  }).then(function(response) {
    var groups = response.message;
    var user_groups = [];
    var promises = [];
    groups.forEach(function(group) {
      promises.push(frappe.call('frappe.client.get', {
        doctype: 'User Group',
        filters: { name: group.name },
        fieldname: ['user_group_members']
      }).then(function(response) {
        var users = response.message.user_group_members;
        
        for (var k = 0; k < users.length; k++) {
          if (users[k].user === assigned_to && !found_groups.includes(response.message.name) && !user_groups.includes(response.message.name)) {
            user_groups.push({user_group: response.message.name});
            found_groups.push(response.message.name); 
          }
        }
      }));
    });
    return Promise.all(promises).then(function() {
      return user_groups;
    });
  });
}

frappe.ui.form.on("Issue", {
    before_save: function(frm) {
        var assignments = frm.get_docinfo().assignments;
        if (assignments === undefined) {
            return;
        }
        if (assignments.length === 0) {
            frm.set_value("custom_z_user_group", "");
        } else {
            var promises = [];
            var found_groups = [];
            for (var i = 0; i < assignments.length; i++) {
                var assigned_to = assignments[i].owner;
                promises.push(getUserGroups(assigned_to, found_groups));
            }
            if (!frm.saving) { 
                frm.saving = true; 
                frappe.run_serially([
                    function() {
                        return Promise.all(promises);
                    },
                    function(user_groups_arr) {
                        var all_user_groups = [].concat.apply([], user_groups_arr);
                        frm.set_value("custom_z_user_group", all_user_groups);
                    },
                    function() {
                        return frm.save();
                    }
                ]).catch(function(error) {
                    console.log(error);
                }).finally(function() {
                    frm.saving = false; 
                });
            }
        }
    }
});

frappe.ui.form.on("Issue", {
    before_save: function(frm) {
        frm.set_df_property("custom_z_user_group", "read_only", 1);
    }
});

frappe.ui.form.on("Issue", "onload", function(frm) {
  frm.set_query("custom_categories", function() {
    return {
      "filters": {
        "parent_solution_dictionary": "Category"
      }
    };
  });
   frm.set_query("custom_directions_", function() {
    return {
      "filters": {
        "parent_solution_dictionary": "Directions"
      }
    };
  });
});

function set_css(frm) {
  var user_roles = frappe.user_roles;
  if (user_roles.includes('Z_Dinord_SAP_Support_Consultant')) {
    var subject_label = document.querySelector("[data-fieldname='subject'] label");
    subject_label.style.fontWeight = 'bold';
    subject_label.style.display = 'block';
    subject_label.style.marginBottom = '5px';
    subject_label.style.color = '#333';

    var issue_type_label = document.querySelector("[data-fieldname='issue_type'] label");
    issue_type_label.style.fontWeight = 'bold';
    issue_type_label.style.display = 'block';
    issue_type_label.style.marginBottom = '5px';
    issue_type_label.style.color = '#333';

    document.querySelectorAll("[data-fieldname ='role']")[1].style.fontWeight = 'bold';
    document.querySelectorAll("[data-fieldname ='type']")[1].style.fontWeight = 'bold';
    document.querySelectorAll("[data-fieldname ='type']")[2].style.fontWeight = 'bold';
    document.querySelectorAll("[data-fieldname ='category1']")[1].style.fontWeight = 'bold';
  }
}

frappe.ui.form.on('Issue', {
  onload: function(frm) {
    set_css(frm);
  }
});

frappe.ui.form.on("Issue", {
    before_save: function(frm, cdt, cdn) {
        if (frm.doc.status === 'Resolved' && frm.doc.project === 'PROJ-0003') {
            toggleGridReqd(frm.fields_dict.identification.grid, 'code', true);
            toggleGridReqd(frm.fields_dict.custom__participants.grid, 'participant', true);
        } else {
            toggleGridReqd(frm.fields_dict.identification.grid, 'code', false);
            toggleGridReqd(frm.fields_dict.custom__participants.grid, 'participant', false);
        }
    }
});

function toggleGridReqd(grid, fieldname, reqd) {
    grid.toggle_reqd(fieldname, reqd);
}

frappe.ui.form.on("Task", {
   after_workflow_action: function(frm) {
       console.log ('Привет');
    if (frm.doc.status_abap == "Выполнено") {
        console.log ('Hello');
      frm.set_value("status", "Completed");
    }
  }
});

frappe.ui.form.on("Issue", {
  status: function(frm) {
    if (["Open", "Replied", "On Hold"].includes(frm.doc.status)) {
      frm.toggle_display("resolution_date", false);
     } else if (frm.doc.status === "Resolved") {
      frm.toggle_display("resolution_date", true);
      frm.set_value("resolution_date", frappe.datetime.now_datetime());
     }  
  }
});
